const rawString =$input.first().json.output;

// 1. Remove backticks, 'json', and escaped \n
const cleanString = rawString
  .replace(/```json|```/g, '')  // Remove ```json and ```
  .replace(/\\n/g, '')          // Remove \n
  .trim();                      // Trim whitespace

// 2. Parse to JSON
try {
  return JSON.parse(cleanString);
} catch (error) {
  // If parsing fails, return error with cleaned string for debugging
  return {
    error: "Failed to parse JSON",
    cleanedString: cleanString,
    details: error.message
  };
}
